std::string defactorize(long n, bool first = true)
{
  std::ostringstream res;
  int i = 1;
  for (; i < n && i < sizeof(d) / sizeof(long); ++i)
    if ((n % d[i]) == 0)
      break;
  if (!first)
    res << " * ";
  if (i < n && i < sizeof(d) / sizeof(long))
  {
    res << d[i];
    if (n != d[i])
      res << defactorize(n / d[i], false);
  }
  else
    res << n;
  return res.str();
}

bool isPrime(long p, int n)
{
  for (int i = 1; i < sizeof(d) / sizeof(long); ++i)
  {
    if (p == d[i])
      return true;
    if ((p % d[i]) == 0)
      return false;
  }
  return true;
}

unsigned int primes[10000000];

int main(void)
{
  int start = 0;
  int finish = 1;
  int offset_index = 0;
  int tb = -1;
  int tf = -1;
  long nf = 0;
  int j = 0;
  long count = 1;
  long chrs = 0;
  long factor = 1;
  long sum = 0;
  primes[0] = 1;
  primes[1] = 2;
  primes[2] = 3;
  int pi = 1;
  std::cout << "[ ";
  ++chrs;
  std::ofstream primes_out;
  primes_out.open("primes");
  for (long p = 2; p <= 100000000;)
  {
    if (p == nf || !isPrime(p, j))
    {
      std::cout << "Failure for: p = " << p << " = " << defactorize(p) << ", nf = " << nf << ", j = " << j << '\n';
      nf = primes[tb] * primes[++tf];
      std::cout << "Next expected failure " << nf << "\n";
    }
    else
    {
      if (pi == sizeof(primes) / sizeof(unsigned int))
	exit(0);
      primes[pi++] = p;
      primes_out << p << ", ";
      std::cout << "+" << count << " (" << p << ") ";
      sum += count;
      if (sum == factor)
      {
	offset_index = start = finish;
	finish = pi - 1;
	std::cout << "] start = " << primes[start] << ", finish = " << primes[finish] << "\n" << std::endl << "[ ";
        factor *= d[j++];
        chrs = 0;
	sum = 0;
	tf = tb++;
	do { nf = primes[tb] * primes[++tf]; } while (nf < p);
	std::cout << "Table now " << primes[tb] << "\n";
	std::cout << "Next expected failure " << nf << "\n";
      }
      if (chrs++ == 5)
      {
	std::cout << std::endl;
        chrs = 0;
      }
      count = 0;
      std::cout << '\n';
    }
    count += primes[offset_index + 1] - primes[offset_index];
    std::cout << "Expected next prime: " << p << " + (" << primes[offset_index + 1] << " - " << primes[offset_index] << ") = " << p + primes[offset_index + 1] - primes[offset_index] << '\n';
    p += primes[offset_index + 1] - primes[offset_index];
    if (++offset_index == finish)
      offset_index = start;
  }
  std::cout << std::endl;
  primes_out.close();
  return 0;
}

